# Tokenizer ve modeli yükle
tokenizer = T5Tokenizer.from_pretrained("ozcangundes/mt5-multitask-qa-qg-turkish")
model = MT5ForConditionalGeneration.from_pretrained("ozcangundes/mt5-multitask-qa-qg-turkish")


device = torch.device("cuda" if torch.cuda.is_available() else "cpu")
model.to(device) 


max_source_length = 128
max_target_length = 128
tokenized_dataset = CustomDataset(data, tokenizer, max_source_length, max_target_length)

train_dataset = tokenized_dataset


batch_size = 5
num_epochs = 50


train_loader = DataLoader(train_dataset, batch_size=batch_size, shuffle=True)

#  öğrenme oranı belirle
optimizer = torch.optim.AdamW(model.parameters(), lr=5e-5)

# Eğitim 
for epoch in range(num_epochs):
    model.train()
    total_loss = 0
    for batch in train_loader:
        input_ids = batch["input_ids"].to(device)
        attention_mask = batch["attention_mask"].to(device)
        labels = batch["labels"].to(device)
        decoder_attention_mask = batch["decoder_attention_mask"].to(device)

        optimizer.zero_grad()

        outputs = model(
            input_ids=input_ids,
            attention_mask=attention_mask,
            labels=labels,
            decoder_attention_mask=decoder_attention_mask
        )

        loss = outputs.loss
        loss.backward()
        optimizer.step()

        total_loss += loss.item()

    average_loss = total_loss / len(train_loader)
    print(f"Epoch {epoch + 1}, Average Loss: {average_loss:.4f}")

# Kayıt
save_path = "F:/MT/Transformer/ModelMT5/mT5_model.pth"  
torch.save(model.state_dict(), save_path)
print(f"Model saved to {save_path}")
